select t.* from (select row_number() over() as rownumber,db.* ,fl.mc as spfl_mc ,shop.mc as shop_mc ,( SELECT isnull(SUM(sl_qm),0) sl_qm FROM ( SELECT id_kcsp id_shopsp,isnull(sl_qm,0) sl_qm FROM tz_sp_kc WHERE id_shop=db.id_shop and id_kcsp=db.id_kcsp UNION ALL SELECT l2.id_kcsp id_shopsp,(-1)*isnull(sl_total,0) sl_qm FROM td_ls_2 l2 left join td_ls_1 l1 on l2.id_bill=l1.id WHERE l2.id_kcsp=db.id_kcsp and l1.id_shop=db.id_shop and not exists(select 1 from td_ls_2c c2 where c2.id=l2.id) ) AS Tmp GROUP BY id_shopsp ) sl_qm ,isnull(kc.je_qm,0) je_qm ,isnull(kc.dj_cb,0) dj_cb ,(select id from tb_shopsp where id_masteruser=#id_masteruser# and flag_state=1 and flag_delete=0 and id_shop=$id_shop_ck$ and id_sp=db.id_sp) id_shopsp_ck ,(select id_kcsp from tb_shopsp where id_masteruser=#id_masteruser# and flag_state=1 and flag_delete=0 and id_shop=$id_shop_ck$ and id_sp=db.id_sp) id_kcsp_ck from as db left join tb_spfl fl on fl.id=db.id_spfl left join tb_shop shop on shop.id=db.id_shop left join tz_sp_kc kc on kc.id_kcsp=db.id_kcsp and kc.id_shop=db.[id_shop] and kc.id_masteruser=db.id_masteruser
) as t
